// Manually construct multipart form data without external dependencies
const http = require('http');

// Get the PDF binary data
const pdfBuffer = Buffer.from($input.first().binary.data.data, 'base64');

// Generate boundary
const boundary = '----FormBoundary' + Math.random().toString(36).substring(2);

// Build multipart form data
const CRLF = '\r\n';
let body = '';
body += '--' + boundary + CRLF;
body += 'Content-Disposition: form-data; name="file"; filename="document.pdf"' + CRLF;
body += 'Content-Type: application/pdf' + CRLF + CRLF;

// Convert to buffer and combine
const headerBuffer = Buffer.from(body, 'utf8');
const footerBuffer = Buffer.from(CRLF + '--' + boundary + '--' + CRLF, 'utf8');
const formData = Buffer.concat([headerBuffer, pdfBuffer, footerBuffer]);

// Return a promise for the HTTP request
return new Promise((resolve, reject) => {
  const options = {
    hostname: 'host.docker.internal',
    port: 8000,
    path: '/analyze',
    method: 'POST',
    headers: {
      'Content-Type': 'multipart/form-data; boundary=' + boundary,
      'Content-Length': formData.length
    }
  };

  const req = http.request(options, (res) => {
    let data = '';

    res.on('data', (chunk) => {
      data += chunk;
    });

    res.on('end', () => {
      try {
        const response = JSON.parse(data);
        resolve({
          json: {
            response: response,
            statusCode: res.statusCode
          }
        });
      } catch (error) {
        resolve({
          json: {
            error: 'Failed to parse response',
            raw_response: data,
            statusCode: res.statusCode
          }
        });
      }
    });
  });

  req.on('error', (error) => {
    resolve({
      json: {
        error: error.message,
        statusCode: 0
      }
    });
  });

  // Send the form data
  req.write(formData);
  req.end();
});

// Process the API response
const data = $input.first().json;

if (data.error) {
  return {
    json: {
      success: false,
      error: data.error,
      raw_response: data.raw_response || null
    }
  };
}

const response = data.response;

if (response && response.findings) {
  const findings = response.findings;

  // Create a summary
  const summary = {
    total_findings: findings.length,
    by_severity: {},
    by_type: {},
    findings: []
  };

  // Process each finding
  findings.forEach(finding => {
    // Count by severity
    summary.by_severity[finding.severity] = 
      (summary.by_severity[finding.severity] || 0) + 1;

    // Count by type
    const findingType = finding.finding_type || finding.type;
    summary.by_type[findingType] = 
      (summary.by_type[findingType] || 0) + 1;

    // Add simplified finding info
    summary.findings.push({
      type: findingType,
      severity: finding.severity,
      page: finding.page_number,
      description: finding.description,
      location: finding.location
    });
  });

  return {
    json: {
      success: true,
      analysis_summary: summary,
      raw_response: response
    }
  };
} else if (response && response.error) {
  // Handle error response
  return {
    json: {
      success: false,
      error: response.error,
      message: "API returned an error"
    }
  };
}

// No findings or unexpected response
return {
  json: {
    success: false,
    error: "Unexpected response format",
    raw_response: response
  }
};

